mapper.select:
select

from user
where
username = #{username,jdbcType=VARCHAR}
AND password = #{password,jdbcType=VARCHAR}

mapper.update:
update user
password=#{password,jdbcType=VARCHAR}, nickname=#{nickname,jdbcType=VARCHAR}, is_admin=#{isAdmin,jdbcType=NUMERIC}, user_rank=#{userRank,jdbcType=VARCHAR}, user_goal=#{userGoal,jdbcType=VARCHAR}, user_port=#{userPort,jdbcType=VARCHAR}, user_friend=#{userFriend,jdbcType=VARCHAR},
where username = #{username,jdbcType=VARCHAR}
